document.addEventListener("DOMContentLoaded",function(){let e=document.getElementById("locationInput"),t=document.getElementById("mapContainer");async function r(e){try{let t=await fetch(`https://nominatim.openstreetmap.org/search?format=json&q=${e}`),r=await t.json();if(r.length>0)return{lat:parseFloat(r[0].lat),lon:parseFloat(r[0].lon)};return console.error("No coordinates found for the specified location."),null}catch(e){throw console.error("Error fetching coordinates:",e),e}}e.addEventListener("change",async function(){let o=e.value;try{let e=await r(o);e?t.innerHTML=`<iframe width="600" height="400" frameborder="0" scrolling="no" marginheight="0" marginwidth="0" src="https://www.openstreetmap.org/export/embed.html?bbox=${e.lon-1}%2C${e.lat-1}%2C${e.lon+1}%2C${e.lat+1}&amp;layer=mapnik&amp;marker=${e.lat}%2C${e.lon}" style="border: 1px solid black"></iframe>`:alert("Could not fetch coordinates. Please try again.")}catch(e){console.error("Error:",e),alert("An error occurred. Please try again.")}})});